-- =====================================================
-- ADD INSTAGRAM POSTS TABLE
-- =====================================================
-- This migration adds a table for managing Instagram posts
-- that will be displayed in the Instagram carousel on the homepage
-- 
-- Version: 1.0
-- Date: 2025-09-15
-- =====================================================

-- Create instagram_posts table
CREATE TABLE IF NOT EXISTS public.instagram_posts (
  id UUID NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,
  embed_html TEXT NOT NULL,
  caption TEXT,
  is_active BOOLEAN DEFAULT TRUE,
  sort_order INTEGER DEFAULT 0,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()
);

-- Add indexes for better performance
CREATE INDEX IF NOT EXISTS idx_instagram_posts_active ON public.instagram_posts (is_active);
CREATE INDEX IF NOT EXISTS idx_instagram_posts_sort_order ON public.instagram_posts (sort_order);

-- Enable RLS (Row Level Security)
ALTER TABLE public.instagram_posts ENABLE ROW LEVEL SECURITY;

-- Create policies for instagram_posts
CREATE POLICY "Allow read access for active instagram posts for everyone"
  ON public.instagram_posts FOR SELECT
  USING (is_active = true);

CREATE POLICY "Allow admin to manage instagram posts"
  ON public.instagram_posts FOR ALL
  USING (EXISTS (
    SELECT 1 FROM public.profiles WHERE id = auth.uid() AND is_admin = true
  ));

